import React from 'react';
import ReactDOM from 'react-dom';
import * as serviceWorker from './serviceWorker';
import './css/reset.css';
import './css/timeline.css';
import './css/login.css';
import {BrowserRouter, Route, Redirect, matchPath} from 'react-router-dom';
import Login from './componentes/Login'
import Logout from './componentes/Logout'
import App from './App';
import {createBrowserHistory} from 'history';

// function verificaAutenticacao(nextState, replace){
//     console.log(nextState);
//     const history = createBrowserHistory();
//     const match = matchPath('timeline', {path: nextState.match.url});
//     const rotaPrivadaDaTimeline = match === null;

//     if(rotaPrivadaDaTimeline && localStorage.getItem('auth-token') != null)
//         return true;
// }

function verificaAutorizacao() {
    const history = createBrowserHistory();
    const match = matchPath(history.location.pathname, {path: '/timeline/:login'});

    const privateRoute = match === null;
    if (privateRoute && localStorage.getItem('auth-token') === null) {
        return false
    }
    return true
}

ReactDOM.render(
    (<BrowserRouter>
        <Route exact path="/" component={Login}/>
        <Route exact path="/logout" component={Logout}/>
        <Route path="/timeline/:login" component={App}/>
        <Route exact path="/timeline" render={() => (
            verificaAutorizacao() ? <App/> :
                <Redirect to={'/?msg=Você precisa estar logado para acessar a timeline'}/>
        )}/>
        {/* <Route exact path="/timeline/:login" render={
            () => verificaAutenticacao() ? 
                <App /> : <Redirect to="/?msg=Você precisa estar logado!"/>
        }/> */}
    </BrowserRouter>), 
    document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
